SET SQL_MODE = "NO_AUTO_VALUE_ON_ZERO";
START TRANSACTION;
SET time_zone = "+00:00";

CREATE DATABASE IF NOT EXISTS `musclewikison` DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci;
USE `musclewikison`;

CREATE TABLE `Exercises` (
  `exercise_name` varchar(64) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `muscle_group` varchar(64) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `difficult` varchar(64) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `equipment` varchar(64) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `guide` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `wikilink_musclegroup` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `key_exercise` varchar(64) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;



